package Practice0925;

public class Test10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 0;
		do {
			System.out.print(i);
			i++;
		} while (i < 10);
		
		System.out.print("\n");
		
		int c = 'a';
		do {
			System.out.printf("%c", c);
			c++;
		} while (c <= 'z');
	}

}
